import { useState, useEffect } from 'react';






const Bos=()=>{
    const [number,setNumber]=useState(1);

    function myFunction() {
        setNumber(number*2)
      }

    useEffect(()=>{  
        
        console.log("number changed");
    },[number])

    return(
        <>
         <button onClick={myFunction}>Click me</button>
        <h1>{number}</h1>
        </>
       
        

    )
}

export default Bos;
