import { useState, useEffect } from 'react';






const Bos=()=>{
    const [number,setNumber]=useState(1);

    function myFunction() {
        setNumber(number*2)
      }

    useEffect(()=>{
        
        console.log("number changed");
    },[number])

    return(
        <button onclick="myFunction()">Click me</button>

    )
}

export default Bos;
